    .navbar ul {
      text-align: center;
      justify-content: center;
    }

    .navbar h1 {
      margin-bottom: 1rem;
    }

    .navbar .welcome {
      display: none;
    }

    /* Profiles Page */
    .profile {
      grid-template-columns: 1fr;
      text-align: center;
    }

    .profile ul {
      display: none;
    }

    /* Profile Page */

    .profile-top img,
    .profile img {
      width: 200px;
      margin: auto;
    }

    .profile-grid {
      grid-template-areas:
        'top'
        'about'
        'exp'
        'edu'
        'github';
    }

    .profile-about .skills {
      flex-direction: column;
    }

    .dash-buttons a {
      display: block;
      width: 100%;
      margin-bottom: 0.2rem;
    }

    .post {
      grid-template-columns: 1fr;
    }
    .post a,
    .post button {
      padding: 0.3rem 0.4rem;
    }
  }

